using Nethereum.ABI.FunctionEncoding;
using System;
using Xunit;

namespace Nethereum.ABI.UnitTests
{
    public class ConstructorCallEncoderTests
    {
        [Fact]
        public void ThrowsWhenByteCodeContainsLibraryPlaceholders()
        {

            var contractByteCode = "__$4f6e1f7166b61d394a3a463d15dc4917b6$____$4f6e1f7166b61d394a3a463d15dc4917b6$__";
            var encodedParameters = "";
            Assert.Throws<Exception>(() => new ConstructorCallEncoder().EncodeRequest(contractByteCode, encodedParameters));
        }
    }
}
