        [FunctionOutput]
        public class GetTestFunctionOuptputDTO
        {
            [Parameter("tuple")]
            public TestStructStrings TestStruct { get; set; }


            [Parameter("int256", "test1", 2)]
            public BigInteger Test1 { get; set; }


            [Parameter("int256", "test2", 3)]
