        public class Id2Function : FunctionMessage
        {
        }

        [Function("id3", "uint256")]
        public class Id3Function : FunctionMessage
        {
        }

        [Function("id4", "string")]
        public class Id4Function : FunctionMessage
        {
        }

        [Function("GetTest")]
