            [Parameter("uint256", "id2", 2)] public BigInteger Id2 { get; set; }
        }

        [Function("TestArray", typeof(TestArrayOuputDTO))]
        public class TestArray : FunctionMessage
        {
        }

        [FunctionOutput]
